client/src/components/DrawingManager.css.content:
.drawing-manager-container {
  height: 100%;
  overflow: hidden;
  display: flex;
  flex-direction: column;
}

.drawing-manager {
  padding: 2rem;
  height: 100%;
  overflow-y: auto;
}

.drawing-mapper-header {
  padding: 1rem 2rem;
  background: white;
  border-bottom: 2px solid #e0e0e0;
  display: flex;
  align-items: center;
  gap: 1rem;
  flex-shrink: 0;
}

.drawing-mapper-header h2 {
  font-size: 1.5rem;
  color: #333;
  margin: 0;
}

.back-to-list-btn {
  padding: 0.5rem 1rem;
  background: #f8f9fa;
  color: #667eea;
  border: 2px solid #e0e0e0;
  border-radius: 6px;
  font-weight: 500;
  transition: all 0.2s;
}

.back-to-list-btn:hover {
  background: #667eea;
  color: white;
  border-color: #667eea;
}

.drawing-header {
  margin-bottom: 2rem;
}

.drawing-header h2 {
  font-size: 1.75rem;
  color: #333;
  margin-bottom: 0.5rem;
}

.drawing-subtitle {
  color: #666;
  margin-bottom: 1.5rem;
}

.upload-drawing-btn {
  padding: 0.75rem 1.5rem;
  background: #667eea;
  color: white;
  border-radius: 8px;
  font-weight: 500;
  transition: all 0.2s;
}

.upload-drawing-btn:hover {
  background: #5568d3;
  transform: translateY(-1px);
  box-shadow: 0 4px 12px rgba(102, 126, 234, 0.3);
}

.drawing-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
  gap: 1.5rem;
}

.no-drawings {
  grid-column: 1 / -1;
  padding: 3rem 1.5rem;
  text-align: center;
  color: #999;
}

.no-drawings .hint {
  font-size: 0.875rem;
  margin-top: 0.5rem;
}

.drawing-card {
  background: white;
  border: 2px solid #e0e0e0;
  border-radius: 12px;
  padding: 1.5rem;
  cursor: pointer;
  transition: all 0.2s;
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
}

.drawing-card:hover {
  border-color: #667eea;
  box-shadow: 0 4px 12px rgba(102, 126, 234, 0.2);
  transform: translateY(-2px);
}

.drawing-card.selected {
  border-color: #667eea;
  background: #e8ebfa;
}

.drawing-preview {
  width: 80px;
  height: 80px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: #f8f9fa;
  border-radius: 8px;
  margin-bottom: 1rem;
}

.drawing-icon {
  font-size: 2.5rem;
}

.drawing-info {
  text-align: center;
  width: 100%;
}

.drawing-name {
  font-weight: 600;
  color: #333;
  margin-bottom: 0.5rem;
  word-break: break-word;
  font-size: 0.9rem;
}

.drawing-type {
  font-size: 0.75rem;
  color: #666;
  text-transform: uppercase;
  font-weight: 500;
  margin-bottom: 0.5rem;
}

.drawing-mapped-badge {
  display: inline-block;
  padding: 0.25rem 0.5rem;
  background: #d4f4dd;
  color: #2d6a3e;
  border-radius: 4px;
  font-size: 0.75rem;
  font-weight: 500;
}

.drawing-actions {
  position: absolute;
  top: 0.5rem;
  right: 0.5rem;
  display: flex;
  gap: 0.25rem;
  opacity: 0;
  transition: opacity 0.2s;
}

.drawing-card:hover .drawing-actions {
  opacity: 1;
}

.rename-drawing-btn,
.delete-drawing-btn {
  padding: 0.5rem;
  background: rgba(255, 255, 255, 0.9);
  font-size: 1rem;
  transition: all 0.2s;
  border-radius: 4px;
  border: 1px solid #e0e0e0;
}

.rename-drawing-btn:hover {
  background: #e8f4ff;
  border-color: #667eea;
  transform: scale(1.1);
}

.delete-drawing-btn:hover {
  background: #fee;
  border-color: #f44;
  transform: scale(1.1);
}

src/types.ts.content:
export interface FieldMapping {
  x: number;
  y: number;
  size: number;
  align?: 'left' | 'center' | 'right';
  color?: string;
  fontFamily?: 'Helvetica' | 'Helvetica-Bold' | 'Helvetica-Oblique' | 'Times-Roman' | 'Times-Bold' | 'Times-Italic' | 'Courier' | 'Courier-Bold';
  maxWidth?: number;
  maxHeight?: number;
  bold?: boolean;
  italic?: boolean;
  condition?: {
    field: string;
    operator: 'equals' | 'not_equals' | 'contains' | 'not_contains';
    value: string;
  };
}

export interface TemplateMapping {
  [fieldName: string]: FieldMapping;
}

export interface DrawingPlacementArea {
  drawingName: string;
  x: number;
  y: number;
  width: number;
  height: number;
  rotation?: number;
  conditionField?: string;
  conditionOperator?: 'equals' | 'not_equals';
  conditionValue?: string;
}

export interface TemplateConfiguration {
  mapping: TemplateMapping;
  drawingPlacements?: DrawingPlacementArea[];
}

export interface DrawingInfo {
  name: string;
  type: 'pdf' | 'image' | 'svg';
  hasMapping: boolean;
}

export interface DrawingInsertion {
  drawing: string;
  x: number;
  y: number;
  width: number;
  height: number;
  rotation?: number;
  data: {
    [key: string]: string;
  };
}

export interface GenerateRequest {
  template: string;
  data: {
    [key: string]: string;
  };
  drawings?: DrawingInsertion[];
}

export interface TemplateInfo {
  name: string;
  hasMapping: boolean;
}

export interface Combination {
  name: string;
  templateName: string;
  drawingPlacements: DrawingPlacementArea[];
}

export interface CombinationInfo {
  name: string;
}

